async function recognizeCustom(file) {
  // Model ID from when you trained your model.
  const endpoint = "https://frisopsrecognizer.cognitiveservices.azure.com/";
  const apiKey = "7569e082df7b45d7b931d214af45e22e";
  const modelId = "3137210a-245f-40b0-813f-987ded086d8a";
  const formUrl = file;

  const client = new FormRecognizerClient(
    endpoint,
    new AzureKeyCredential(apiKey)
  );

  const poller = await client.beginRecognizeCustomFormsFromUrl(
    modelId,
    formUrl,
    {
      onProgress: (state) => {
        console.log(`status: ${state.status}`);
      },
    }
  );
  const forms = await poller.pollUntilDone();

  console.log("Forms:");
  for (const form of forms || []) {
    console.log(`${form.formType}, page range: ${form.pageRange}`);
    console.log("Pages:");
    for (const page of form.pages || []) {
      console.log(`Page number: ${page.pageNumber}`);
      console.log("Tables");
      for (const table of page.tables || []) {
        for (const cell of table.cells) {
          console.log(
            `cell (${cell.rowIndex},${cell.columnIndex}) ${cell.text}`
          );
        }
      }
    }

    console.log("Fields:");
    for (const fieldName in form.fields) {
      // each field is of type FormField
      const field = form.fields[fieldName];
      console.log(
        `Field ${fieldName} has value '${field.value}' with a confidence score of ${field.confidence}`
      );
    }
  }

  return forms;
}

