router.post("/", uploadStrategy, async (req, res) => {
  //console.log(req.query.user)
  if (req != null && req.files != null && req.files.length > 0) {
    for (var i = 0; i < req.files.length; i++) {
      var blobName = getBlobName(req.files[i].originalname);
      var stream = getStream(req.files[i].buffer);
      var containerClient = blobServiceClient.getContainerClient(containerName);
      var blockBlobClient = containerClient.getBlockBlobClient(blobName);
      try {
        await blockBlobClient.uploadStream(stream);
        await frisopsupload.create({
          url: blockBlobClient.url,
          filename: blobName,
          control_id: nanoid(7),
          scores: 10,
          initiator: req.query.user,
          filetype: req.query.file,
        });
        if (i === req.files.length - 1) {
          res.render("success", {
            message: "File uploaded to Azure Blob storage.",
          });
        }
      } catch (err) {
        res.render("error", { message: err.message });
      }
    }
  }
});

