router.put("/requisitions/:id", async (req, res) => {
  try {
    let uuid = req.params.id;
    const { status } = req.body;

    let progress = 0;

    await requistionLogs
      .findAll({
        attributes: ["count_of_items"],
        where: {
          uuid,
        },
      })
      .then((items) => {
        // progress = Math.floor(100 / item.count_of_items);
        items.forEach((item) => {
          // console.log(item.count_of_items);

          progress = Math.floor(100 / item.count_of_items);
        });
        // console.log(item.count_of_items);
        //console.log(progress);
      });

    const updateLog = await requistionLogs.update(
      { status, button_ui: true, progress_approved: progress },
      {
        where: {
          uuid,
        },
      }
    );

    // await Feeds.update(
    //   { status },
    //   {
    //     where: {
    //       uuid,
    //     },
    //   }
    // );

    //console.log(updateLog);

    return res.status(201).json({ data: updateLog });
  } catch (error) {}
  //console.log(req.params.id)
});

